package JavaClasses;import java.io.Serializable;import java.util.UUID;public class Person implements Serializable{    private String name;    private Integer height;    private String passportID;    private Location location;    private static int id = 0;    public Person(String name, Integer height, Location location){        this.name = name;        this.height = height;        this.passportID = UUID.randomUUID().toString();        this.location = location;    }    @Override    public String toString(){        return "\nИмя : " + name +                "\nРост : "+ height +                "\nID паспорта : " + passportID +                "\nМестоположение : " + location + "\n";    }    }